--  DDL for View PATIENTS_HEALTH_RECORDS
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "PATIENTS_HEALTH_RECORDS" ("FIRSTNAME", "LASTNAME", "HEALTHCARDNUMBER", "VITALINFO") AS 
  SELECT
    p.FIRSTNAME,
    p.LASTNAME,
    p.HEALTHCARDNUMBER,
    hr.VITALINFO
FROM 
    PATIENT p
INNER JOIN 
    HEALTH_RECORD hr
ON
    p.HEALTHCARDNUMBER = hr.HEALTHCARDNUMBER
;
REM INSERTING into PATIENTS_HEALTH_RECORDS
SET DEFINE OFF;
